$(function () {
  $(function () {
    $(".sep-menu-box .bottom .menu-item>li .box i").click(function () {
      $(this).parent().parent().hasClass("active")
        ? ($(this).parent().siblings("ul").slideUp(),
          $(this).parent().parent().removeClass("active"))
        : ($(this).parent().parent().addClass("active"),
          $(this).parent().siblings("ul").slideDown(),
          $(this).parent().parent().siblings().find("ul").slideUp(),
          $(this).parent().parent().siblings("li").removeClass("active"));
    }),
      $(".sep-menu-box .bottom-toggle .toggle ul li span").click(function () {
        $(".sep-menu-box .bottom").removeClass("no"),
          $(".sep-menu-box .bottom-toggle").removeClass("active");
      }),
      $(".sep-search-box .i-closes").click(function () {
        $(".sep-search-box").fadeOut(), $(".sep-shadow").fadeOut();
      }),
      $("#menu").click(function () {
        $(".sep-menu-box").addClass("active"), $(".sep-shadow").fadeIn();
      }),
      $("#menuClose").click(function () {
        $(".sep-menu-box").removeClass("active"), $(".sep-shadow").fadeOut();
      }),
      $("#headerSearch").click(function () {
        $(".sep-menu-box").removeClass("active"), $(".sep-search-box").fadeIn();
      }),
      $("#headerSearch1").click(function () {
        $(".sep-menu-box").removeClass("active"), $(".sep-search-box").fadeIn();
      }),
      $("#global").click(function () {
        $(".multi-language").slideToggle();
      }),
      $(".mb-bottom-wrap .bottom-menu>li .box i").click(function () {
        $(this).parent().parent().hasClass("active")
          ? ($(this).parent().siblings("ul").slideUp(),
            $(this).parent().parent().removeClass("active"))
          : ($(this).parent().parent().addClass("active"),
            $(this).parent().siblings("ul").slideDown(),
            $(this).parent().parent().siblings().find("ul").slideUp(),
            $(this).parent().parent().siblings("li").removeClass("active"));
      }),
      $("#headerSearch1").click(function () {
        $(".sep-menu-box").removeClass("active"), $(".sep-search-box").fadeIn();
      });
  }),
    $(".mp_bottom_up_item_icon").click(function () {
      var a = $(this).hasClass("fa-angle-up");
      a
        ? $(this).removeClass("fa-angle-up").addClass("fa-angle-down")
        : $(this).removeClass("fa-angle-down").addClass("fa-angle-up"),
        $(this).parent().find(".mp_bottom_up_item_href_group").slideToggle();
    }),
    $("table").wrap("<div class='overflow-x'></div>"),
    $(
      ".bottom .bottom_up .up_container .bottom_item .bottom_item_title i"
    ).click(function (a) {
      a.preventDefault(),
        $(this).parent().parent().hasClass("ons")
          ? ($(this).parent().parent().removeClass("ons"),
            $(this).parent().siblings(".child_group").slideUp(200))
          : ($(this).parent().parent().addClass("ons"),
            $(this).parent().siblings(".child_group").slideDown(200));
    }),
    $(window).scroll(function () {
      $(window).scrollTop() > 100
        ? ($(".sep-header").addClass("onss"),
          $(".sep-lx-header-mb").addClass("onss"))
        : ($(".sep-header").removeClass("onss"),
          $(".sep-lx-header-mb").removeClass("onss"));
    });
});
